function respondWithResult(res, statusCode) {
  statusCode = statusCode || 200;
  return function(entity) {
    if (entity) {
      res.status(statusCode).json(entity);
    }
  };
}

function saveUpdates(updates) {
  return function(entity) {
    return entity.updateAttributes(updates)
      .then(updated => {
        return updated;
      });
  };
}

function removeEntity(res) {
  return function(entity) {
    if (entity) {
      return entity.destroy()
        .then(() => {
          res.status(204).end();
        });
    }
  };
}

function handleEntityNotFound(res) {
  return function(entity) {
    if (!entity) {
      res.status(404).end();
      return null;
    }
    return entity;
  };
}

function handleError(res, statusCode) {
  statusCode = statusCode || 500;
  return function(err) {
    res.status(statusCode).send(err);
  };
}

// Gets a list of Payments
export function index(req, res) {
  Payment.findAll()
    .then(respondWithResult(res))
    .catch(handleError(res));
}

// Gets a single Payment from the DB
export function show(req, res) {
  Payment.find({
      where: {
        id: req.params.id
      }
    })
    .then(handleEntityNotFound(res))
    .then(respondWithResult(res))
    .catch(handleError(res));
}

// Creates a new Payment in the DB
export function paymentSuccess(req, res) {
    
  PaymentTransactionSession.find({

      where: {
        transaction_id: req.body.txnid,
        active: true
      },
      raw: true
    })
    .then(function(transaction) {


      if (transaction === null) {

        res.status(500).json("Invalid Transaction")
        return;
      }
      console.log("type of addresses ", typeof transaction.address_index)
      var script = 'select addresses[' + (transaction.address_index + 1) + '] from "user" where active=true and id=' + transaction.user_id;

      db.sequelize.query(script)
        .then(function(address) {


          var deliveryAddress = address[0][0].addresses;
          console.log(deliveryAddress);
          orderController.createOrder(transaction.user_id, deliveryAddress, 'payment success')
            .then(function(orderId) {

              console.log("o id",orderId)
              Order.find({

                attributes: ['order_number'],
                where: {

                  id: orderId
                },
                raw: true
              }).then(function(order) {
               
                var payment = {}
                payment.order_id = orderId;
                payment.payment_details = req.body;

                Payment.create(payment)
                  .then(function(payment) {
                   

                    res.redirect(req.protocol + '://' + req.get('host') + '/checkout?status=success&ordernumber='+order.order_number);
                    createPDF(payment.order_id);
                    sendSms(payment.order_id);
                  })
              })
            })
            .catch(function(err){

              console.log("err ",err)
            })
        })
    })
}
