export function paymentFailure(req, res) {


  PaymentTransactionSession.find({

      where: {
        transaction_id: req.body.txnid,
        active: true
      },
      raw: true
    })
    .then(function(transaction) {

      console.log("t t ", transaction)

      if (transaction === null) {

        res.status(500).json("Invalid Transaction")
        return;
      }
      console.log("type of addresses ", typeof transaction.address_index)
      var script = 'select addresses[' + (transaction.address_index + 1) + '] from "user" where active=true and id=' + transaction.user_id;

      db.sequelize.query(script)
        .then(function(address) {

          console.log("address ", JSON.stringify(address[0][0]))
          var deliveryAddress = address[0][0].addresses;

          orderController.createOrder(transaction.user_id, deliveryAddress, 'payment failed')
            .then(function(orderId) {
              
              Order.find({

                attributes: ['order_number'],
                where: {

                  id: orderId
                },
                raw: true
              })
              .then(function(order){
                  
                console.log("done")
                var payment = {}
                payment.order_id = orderId;
                payment.payment_details = req.body;

                Payment.create(payment)
                .then(function(payment) {

                res.redirect(req.protocol + '://' + req.get('host') + '/checkout?status=failure&ordernumber='+order.order_number);
                })
              })

              
            })
        })
    })
}


// Updates an existing Payment in the DB
export function update(req, res) {
  if (req.body.id) {
    delete req.body.id;
  }
  Payment.find({
      where: {
        id: req.params.id
      }
    })
    .then(handleEntityNotFound(res))
    .then(saveUpdates(req.body))
    .then(respondWithResult(res))
    .catch(handleError(res));
}

// Deletes a Payment from the DB
export function destroy(req, res) {
  Payment.find({
      where: {
        id: req.params.id
      }
    })
    .then(handleEntityNotFound(res))
    .then(removeEntity(res))
    .catch(handleError(res));
}